// Extract ready tasks and create individual items for processing
const data = $input.item.json;
const readyTasks = data.readyTasks || [];

if (readyTasks.length === 0) {
  // No ready tasks, return the original data for final processing
  return data;
}

// Get the first ready task to process
const currentTask = readyTasks[0];

// Return the task data along with the original registry information
return {
  ...currentTask,
  originalData: data
};

// Update task registry with completed task result
const taskResult = $input.first().json.output || $input.first().json;
const executedTask = $input.last().json;

// Get original task data
const originalData = executedTask.originalData;

// Clone existing registry state
const taskRegistry = { ...originalData.taskRegistry };
const completedTasks = [...originalData.completedTasks];
const pendingTasks = [...originalData.pendingTasks];

// ✅ Mark current task as completed
if (taskRegistry[taskResult.task_id]) {
  taskRegistry[taskResult.task_id].status = taskResult.task_status;
  taskRegistry[taskResult.task_id].output = taskResult.task_output;

  if (taskResult.task_status === "success") {
    if (!completedTasks.includes(taskResult.task_id)) {
      completedTasks.push(taskResult.task_id);
    }
    const pendingIndex = pendingTasks.indexOf(taskResult.task_id);
    if (pendingIndex > -1) {
      pendingTasks.splice(pendingIndex, 1);
    }
  }
}

// ✅ Resolve any $ref references in params
function resolveParamRefs(params, taskRegistry) {
  const resolved = {};

  for (const [key, value] of Object.entries(params)) {
    if (typeof value === "object" && value !== null && "$ref" in value) {
      const ref = value.$ref;

      // 🔍 Extract dependency ID or numeric index
      // supports: "#/tasks/0/results/sum" or "#/tasks/task_1/results"
      let depTaskId = null;
      const pathParts = ref.split("/").filter(Boolean);
      const taskIndex = pathParts.findIndex(p => p === "tasks");

      if (taskIndex !== -1 && pathParts.length > taskIndex + 1) {
        const nextPart = pathParts[taskIndex + 1];
        // If it's a number, map index → task_id from registry order
        if (/^\d+$/.test(nextPart)) {
          const allTaskIds = Object.keys(taskRegistry);
          depTaskId = allTaskIds[parseInt(nextPart, 10)];
        } else {
          depTaskId = nextPart;
        }
      }

      if (depTaskId && taskRegistry[depTaskId]?.output) {
        const depOutput = taskRegistry[depTaskId].output;
        resolved[key] = depOutput?.result ?? depOutput;
      } else {
        resolved[key] = null;
      }
    } else {
      resolved[key] = value;
    }
  }

  return resolved;
}

// ✅ Find ready tasks (dependencies completed)
const readyTasks = [];
for (const taskId of pendingTasks) {
  const task = taskRegistry[taskId];
  if (task.status === "pending") {
    const allDependenciesMet = task.dependencies.every(
      depId =>
        completedTasks.includes(depId) &&
        taskRegistry[depId]?.status === "success"
    );

    if (allDependenciesMet) {
      const enrichedTask = JSON.parse(JSON.stringify(task));
      enrichedTask.params = resolveParamRefs(enrichedTask.params, taskRegistry);
      readyTasks.push(enrichedTask);
    }
  }
}

// ✅ Return updated workflow state
return {
  taskRegistry,
  pendingTasks,
  completedTasks,
  readyTasks,
  currentBatch: (originalData.currentBatch || 0) + 1,
  totalTasks: originalData.totalTasks,
  lastTaskResult: taskResult
};

// Extract tasks and prepare for dependency resolution
const tasks = $input.item.json.task_plan.tasks;

// Create a task registry with status tracking
const taskRegistry = {};
const pendingTasks = [];
const completedTasks = [];

// Initialize all tasks
for (const task of tasks) {
  taskRegistry[task.task_id] = {
    ...task,
    status: 'pending',
    output: null,
    dependencies: task.dependencies || []
  };
  pendingTasks.push(task.task_id);
}

// Find tasks that can be executed immediately (no dependencies)
const readyTasks = [];
for (const taskId of pendingTasks) {
  const task = taskRegistry[taskId];
  if (task.dependencies.length === 0) {
    readyTasks.push(task);
  }
}

return {
  chatInput: $input.item.json.chatInput,
  taskRegistry,
  pendingTasks,
  completedTasks,
  readyTasks,
  currentBatch: 0,
  totalTasks: tasks.length
};

let finalResponse = {}

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  finalResponse = {...finalResponse, ...item.json }
}
return {...finalResponse};

// Collect all completed task results for final response generation
const taskRegistry = $input.item.json.taskRegistry;
const completedTasks = $input.item.json.completedTasks;

const taskResults = [];
for (const taskId of completedTasks) {
  const task = taskRegistry[taskId];
  if (task && task.status === 'success') {
    taskResults.push({
      task_description: task.task_description,
      task_input: task.task_input,
      task_tool: task.tool,
      task_output: task.output,
      task_status: task.status,
      task_id: taskId
    });
  }
}

return {
  taskResults,
  totalTasksCompleted: completedTasks.length,
  allTasksCompleted: true
};
